import React, { useState } from 'react';
import { ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import theme from './theme';
import EmailOtpFlow from './components/EmailOtpFlow';
import ApplicationFlow from './components/ApplicationFlow';

function App() {
  const [verifiedEmail, setVerifiedEmail] = useState(null);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="sticky" color="primary" elevation={2} sx={{ mb: 4 }}>
        <Toolbar sx={{ justifyContent: 'center', minHeight: 72 }}>
          <Typography
            variant="h4"
            component="div"
            sx={{
              fontWeight: 900,
              fontSize: { xs: '1.3rem', sm: '2rem', md: '2.3rem' },
              letterSpacing: 2,
              color: '#fff',
              textAlign: 'center',
              textShadow: '0 2px 8px rgba(0,64,128,0.10)',
              position: 'relative',
              pb: 0.5,
              '&:after': {
                content: '""',
                display: 'block',
                width: 60,
                height: 4,
                background: 'linear-gradient(90deg, #008080 0%, #00bfae 100%)',
                borderRadius: 2,
                margin: '8px auto 0 auto',
              },
            }}
          >
            Online Application Processing System
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '80vh', justifyContent: 'center' }}>
        <Paper elevation={3} sx={{ width: '100%', p: { xs: 2, sm: 4 }, mt: 4, mb: 4, borderRadius: 3 }}>
          {!verifiedEmail ? (
            <EmailOtpFlow onVerified={setVerifiedEmail} />
          ) : (
            <ApplicationFlow email={verifiedEmail} />
          )}
        </Paper>
      </Container>
      <div className="footer">Â© 2025 Online Application Processing System. All rights reserved.</div>
    </ThemeProvider>
  );
}

export default App;
