import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { TextField, Button, Box, Typography, Alert } from '@mui/material';
import api from '../api';

export default function EmailOtpFlow({ onVerified }) {
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState('');
  const [serverMsg, setServerMsg] = useState('');
  const [error, setError] = useState('');
  const { register, handleSubmit } = useForm();

  const handleEmail = async (data) => {
    setError('');
    setServerMsg('');
    try {
      await api.post('/otp/request', { email: data.email });
      setEmail(data.email);
      setStep(2);
      setServerMsg('OTP sent to your email.');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to send OTP');
    }
  };

  const handleOtp = async (data) => {
    setError('');
    setServerMsg('');
    try {
      await api.post('/otp/verify', { email, otp: data.otp });
      setServerMsg('OTP verified!');
      onVerified(email);
    } catch (err) {
      setError(err.response?.data?.error || 'OTP verification failed');
    }
  };

  return (
    <div className="modern-card">
      <Box maxWidth={400} mx="auto" mt={4}>
        <Typography variant="h5" mb={2}>Online Application - Email Verification</Typography>
        {step === 1 && (
          <form onSubmit={handleSubmit(handleEmail)}>
            <TextField
              label="Email"
              fullWidth
              margin="normal"
              {...register('email', { required: true, pattern: /.+@.+\..+/ })}
            />
            <Button type="submit" variant="contained" fullWidth>Send OTP</Button>
          </form>
        )}
        {step === 2 && (
          <form onSubmit={handleSubmit(handleOtp)}>
            <TextField
              label="Enter OTP"
              fullWidth
              margin="normal"
              {...register('otp', { required: true })}
            />
            <Button type="submit" variant="contained" fullWidth>Verify OTP</Button>
          </form>
        )}
        {serverMsg && <Alert severity="success" sx={{ mt: 2 }}>{serverMsg}</Alert>}
        {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
      </Box>
    </div>
  );
}
