import React, { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { Box, Typography, MenuItem, Select, Button, TextField, Alert } from '@mui/material';
import api from '../api';

const vacancyForms = {
  'Management Assistant': [
    { name: 'fullName', label: 'Full Name', required: true },
    { name: 'contact', label: 'Contact Number', required: true },
    { name: 'qualification', label: 'Highest Qualification', required: true },
    { name: 'experience', label: 'Relevant Experience (years)', required: true },
  ],
  'Works Aide': [
    { name: 'fullName', label: 'Full Name', required: true },
    { name: 'contact', label: 'Contact Number', required: true },
    { name: 'physicalFitness', label: 'Are you physically fit?', required: true },
  ],
  'Store Keeper': [
    { name: 'fullName', label: 'Full Name', required: true },
    { name: 'contact', label: 'Contact Number', required: true },
    { name: 'qualification', label: 'Relevant Qualification', required: true },
    { name: 'inventoryExp', label: 'Inventory Management Experience (years)', required: true },
  ],
  'Driver': [
    { name: 'fullName', label: 'Full Name', required: true },
    { name: 'contact', label: 'Contact Number', required: true },
    { name: 'licenseNo', label: 'Driving License Number', required: true },
    { name: 'licenseType', label: 'License Type', required: true },
    { name: 'drivingExp', label: 'Driving Experience (years)', required: true },
  ],
};

export default function ApplicationFlow({ email }) {
  const [ad, setAd] = useState(null);
  const [applicationType, setApplicationType] = useState('Internal');
  const [vacancyType, setVacancyType] = useState('');
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const { register, handleSubmit, reset } = useForm();

  useEffect(() => {
    api.get('/advertisements').then(res => {
      setAd(res.data[0]);
    });
  }, []);

  const onSubmit = async (data) => {
    setError('');
    setSuccess('');
    try {
      await api.post('/applications', {
        email,
        advertisementId: ad._id,
        vacancyType,
        applicationType,
        formData: data,
      });
      setSuccess('Application submitted successfully!');
      reset();
    } catch (err) {
      setError(err.response?.data?.error || 'Submission failed');
    }
  };

  if (!ad) return <Typography>Loading...</Typography>;

  return (
    <div className="modern-card">
      <Box maxWidth={500} mx="auto" mt={4}>
        <Typography variant="h5" mb={2}>Apply for: {ad.date}</Typography>
        <Box mb={2}>
          <Typography>Select Application Type:</Typography>
          <Select value={applicationType} onChange={e => setApplicationType(e.target.value)} fullWidth>
            <MenuItem value="Internal">Internal</MenuItem>
            <MenuItem value="External">External</MenuItem>
          </Select>
        </Box>
        <Box mb={2}>
          <Typography>Select Vacancy:</Typography>
          <Select value={vacancyType} onChange={e => setVacancyType(e.target.value)} fullWidth>
            {ad.vacancyTypes.map(v => (
              <MenuItem key={v} value={v}>{v}</MenuItem>
            ))}
          </Select>
        </Box>
        {vacancyType && (
          <form onSubmit={handleSubmit(onSubmit)}>
            {vacancyForms[vacancyType].map(field => (
              <TextField
                key={field.name}
                label={field.label}
                fullWidth
                margin="normal"
                {...register(field.name, { required: field.required })}
              />
            ))}
            <Button type="submit" variant="contained" fullWidth>Submit Application</Button>
          </form>
        )}
        {success && <Alert severity="success" sx={{ mt: 2 }}>{success}</Alert>}
        {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
      </Box>
    </div>
  );
}
