import React, { useState } from 'react';
import { ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import theme from './theme';
import EmailOtpFlow from './components/EmailOtpFlow';
import ApplicationFlow from './components/ApplicationFlow';

function App() {
  const [verifiedEmail, setVerifiedEmail] = useState(null);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="sticky" color="primary" elevation={2} sx={{ mb: 4 }}>
        <Toolbar sx={{ justifyContent: 'center', minHeight: 80 }}>
          <Box
            sx={{
              px: 4,
              py: 1.5,
              bgcolor: '#fff',
              borderRadius: '32px',
              border: '2.5px solid #008080',
              boxShadow: '0 2px 12px rgba(0,128,128,0.08)',
              display: 'inline-block',
            }}
          >
            <Typography
              variant="h6"
              component="div"
              sx={{
                color: '#004080',
                fontWeight: 800,
                letterSpacing: 1.5,
                fontSize: { xs: '1.1rem', sm: '1.35rem', md: '1.5rem' },
                textAlign: 'center',
                textShadow: '0 1px 0 #e0e7ef',
              }}
            >
              Online Application Processing System
            </Typography>
          </Box>
        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '80vh', justifyContent: 'center' }}>
        <Paper elevation={3} sx={{ width: '100%', p: { xs: 2, sm: 4 }, mt: 4, mb: 4, borderRadius: 3 }}>
          {!verifiedEmail ? (
            <EmailOtpFlow onVerified={setVerifiedEmail} />
          ) : (
            <ApplicationFlow email={verifiedEmail} />
          )}
        </Paper>
      </Container>
      <div className="footer">Â© 2025 Online Application Processing System. All rights reserved.</div>
    </ThemeProvider>
  );
}

export default App;
